mapper.update:
update payment set         
 status='지급완료', updatedate = now()
where amount_date=STR_TO_DATE (#{amount_date}, '%Y-%m-%d') and member_id = #{member_id}

update sales set         
 status='지급완료', updatedate = now()
where expected_date=STR_TO_DATE (#{amount_date}, '%Y-%m-%d') and sales_member = #{member_id}
